import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  //Header huolehtii kurssin nimen renderöimisestä
  return (
    <>
      <h1>{props.course}</h1>
    </>
  )
}
const Content = parts => {
//Content osista ja niiden tehtävämääristä
  return (
    <>
      <Part part1={parts[0].name} exercises1={parts[0].exercises}/>
      <Part part2={parts[1].name} exercises2={parts[1].exercises}/>
      <Part part3={parts[2].name} exercises3={parts[2].exercises}/>
    </>
  )
}

const Part = (props) => {
    return (
      <>
        <p>{props.part1} {props.exercises1}</p>
        <p>{props.part2} {props.exercises2}</p>
        <p>{props.part3} {props.exercises3}</p> 
      </>
    )
  }

const Total = parts => {
//Total tehtävien yhteismäärästä
  return (
    <>
      <p>Number of exercises {parts[0].exercises + parts[1].exercises + parts[2].exercises}</p>
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]


  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
